	import {capitalize} from '@svizzle/utils';
	import ExternalLink from '@svizzle/ui/src/ExternalLink.svelte';
	import Icon from '@svizzle/ui/src/icons/Icon.svelte'
	import Download from '@svizzle/ui/src/icons/feather/Download.svelte';
	import Menu from '@svizzle/ui/src/icons/feather/Menu.svelte';
	import MessageSquare from '@svizzle/ui/src/icons/feather/MessageSquare.svelte';
	import X from '@svizzle/ui/src/icons/feather/X.svelte';

	import {version} from 'app/utils/version';

	const changelogUrl = 'https://github.com/nestauk/eurito_indicators_ui/blob/staging/CHANGELOG.md';

	// shape at https://github.com/nestauk/svizzle/tree/dev/packages/components/ui/src/gauges/screen#screen-store
	export let screen;
	export let segment;

	let isMenuOpen = false;

	function handleClick () {
		isMenuOpen = !isMenuOpen;
	}
	const closeMenu = () => isMenuOpen = false;
</script>

