		<div class='menu-icon' on:click={handleClick}>
			{#if isMenuOpen}
				<Icon glyph={X} />
			{:else}
				<Icon glyph={Menu} />
			{/if}
		</div>
	{/if}
	{#if !screen?.sizeFlags.medium && isMenuOpen || screen?.sizeFlags.medium}
		<menu on:click={closeMenu}>
			<div>
				<ul>
					<li class:selected='{segment === undefined}'>
						<a href='.'>Home</a>
					</li>
					<li class:selected='{segment === 'methodology'}'>
